import { createStore } from "redux";

var initilState = {
  status: false,
  sort: {
    by: "name",
    value: 1,
  },
};

var myReducer = (state = initilState, action) => {
  return state;
};

// thực hiện thay đổi trạng thái
var action = {
  type: "TOOGLE_STATUS",
};

const store = createStore(myReducer);
console.log("Default : ", store.getState());
