    //     tasksEditing: null,
    //   });
    // }
    this.props.onToggleForm();
  };

  onShowForm = () => {
    this.setState({
      isDisplayForm: true,
    });
  };

  // onUpdateStatus = (id) => {
  //   var { tasks } = this.state;
