import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPencilAlt, faTrash } from "@fortawesome/free-solid-svg-icons";
import { useDispatch } from "react-redux";
import * as actions from "../actions/index";

function TaskItem(props) {
  const dispatch = useDispatch();

  const onUpdateStatus = () => {
    dispatch(actions.updateStatus(id));
    props.onUpdateStatus(props.task.id);
  };

  const onDelete = () => {
    props.onDelete(props.task.id);
  };

  const onUpdate = () => {
    props.onUpdate(props.task.id);
  };

  var { task, index } = props;
  return (
    <tr>
      <td>{index + 1}</td>
      <td>{task.name}</td>
      <td className="text-center">
        <span
          className={task.status ? "label label-danger" : "label label-success"}
          onClick={onUpdateStatus}
        >
          {task.status ? "Kích hoạt" : "Ẩn"}
        </span>
      </td>
      <td className="text-center">
        <button type="button" className="btn btn-warning" onClick={onUpdate}>
          <FontAwesomeIcon icon={faPencilAlt} />
          {" Sửa"}
        </button>
        &nbsp;
        <button type="button" className="btn btn-danger" onClick={onDelete}>
          <FontAwesomeIcon icon={faTrash} />
          {" Xóa"}
        </button>
      </td>
    </tr>
  );
}

const mapDispatchToProps = (dispatch, props) => {
  return {
    onUpdateStatus: (id) => {
      dispatch(actions.updateStatus(id));
    },
    onDeleteTask: (id) => {
      dispatch(actions.deleteTask(id));
    },
    onCloseForm: () => {
      dispatch(actions.closeForm());
    },
    onOpenForm: () => {
      dispatch(actions.openForm());
    },
    onEditTask: (task) => {
      dispatch(actions.editTask(task));
    },
  };
};

export default TaskItem;
