import React, { Component } from "react";
import "./App.css";
import Product from "./components/Product";

class App extends Component {
  onClick() {
    alert("This is app component");
  }
  onAddProduct = () => {
    console.log(this.refs.name.value);
  };
  render() {
    var products = [
      {
        id: 1,
        name: "Iphone 12 Pro MAX",
        image:
          "https://hoanghamobile.com/tin-tuc/wp-content/uploads/2020/10/da-co-thiet-ke-mau-sac-va-ngay-ra-mat-chinh-thuc-cua-iphone-12-pro3.jpg",
        price: 20000000,
        status: true,
      },
      {
        id: 2,
        name: "Samsung Galaxy J6",
        image:
          "data:image/jpeg;base64,",
        price: 10000000,
        status: true,
      },
      {
        id: 3,
        name: "Oppo F7",
        image:
          "https://vn-test-11.slatic.net/p/31411ce8b2dc7ac0540df818de835739.jpg_720x720q80.jpg_.webp",
        price: 20000000,
        status: true,
      },
    ];

    var elements = products.map((product, index) => {
      let result = "";
      if (product.status) {
        result = (
          <Product key={product.id} price={product.price} image={product.image}>
            {product.name}
          </Product>
        );
      }
      return result;
    });
    return (
      <div className="App">
        <nav className="navbar navbar-inverse">
          <a className="navbar-brand" href="#">
            State
          </a>
        </nav>

        <div className="container">
          <div className="row">
            <div className="row">
              <table class="table table-hover">
                <thead>
                  <tr>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td></td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
