import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [
        {
          id: 1,
          name: "Iphone 12 Pro MAX",
          image:
            "https://hoanghamobile.com/tin-tuc/wp-content/uploads/2020/10/da-co-thiet-ke-mau-sac-va-ngay-ra-mat-chinh-thuc-cua-iphone-12-pro3.jpg",
          price: 20000000,
          status: true,
        },
        {
          id: 2,
          name: "Samsung Galaxy J6",
          image:
            "data:image/jpeg;base64,",
          price: 10000000,
          status: true,
        },
        {
          id: 3,
          name: "Oppo F7",
          image:
            "https://vn-test-11.slatic.net/p/31411ce8b2dc7ac0540df818de835739.jpg_720x720q80.jpg_.webp",
          price: 20000000,
          status: true,
        },
      ],
      isActive: true,
    };
    this.onSetState = this.onSetState.bind(this);
  }

  onSetState() {
    // if (this.state.isActive === true) {
    //   this.setState({
    //     isActive: false,
    //   });
    // } else {
    //   this.setState({
    //     isActive: true,
    //   });
    // }
    this.setState({
      isActive: !this.state.isActive,
    });
  }
  render() {
    var elements = this.state.products.map((product, index) => {
      let result = "";
      if (product.status) {
        result = (
          <tr key={index}>
            <td>{index}</td>
            <td>{product.name}</td>
            <td>
              <span className="label label-success">{product.price} VNĐ</span>
            </td>
          </tr>
        );
      }
      return result;
    });
    return (
      <div className="App">
        <nav className="navbar navbar-inverse">
          <a className="navbar-brand">State</a>
        </nav>

        <div className="container">
          <div className="row">
            <div className="row">
              <table className="table table-bordered table-hover">
                <thead>
                  <tr>
                    <th>STT</th>
                    <th>Tên sản phẩm</th>
                    <th>Giá</th>
                  </tr>
                </thead>
                <tbody>{elements}</tbody>
              </table>

              <button
                type="button"
                className="btn btn-warning"
                onClick={this.onSetState}
              >
                Active : {this.state.isActive === true ? "true" : "false"}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
